import { useState, useEffect } from 'react'
import styled from 'styled-components'
import Deductions from '../components/deductions'

const Layout = styled.div`
  display: flex;
  flex-direction: column;
  padding: 10px;
`

const Form = styled.div`
  display: flex;
  flex-direction: column;
  width: 300px;
`

const Input = styled.input`
  float: right;
`

const AddItemForm = styled.div`
  flex-direction: row;
`

const InputButton = styled.div`
  float: right;
`

const Home = () => {
  const [deductionInfo, setDeductionInfo] = useState({
    Sawyer: 0,
    Wyatt: 0,
    Jack: 0,
  })

  const updateInfo = (name, list) => {
    const info = { ...deductionInfo }
    const total = list.reduce((a, b) => a + b, 0)
    info[name] = total
    setDeductionInfo(info)
  }

  return (
    <Layout>
      <h1>Grocery Calculator</h1>
      <Form>
        <div>
          Total (including tax):{' '}
          <Input type='text' placeholder='Enter Dollar Value' />
        </div>
        <div>
          Tax: <Input type='text' placeholder='Enter Dollar Value' />
        </div>
      </Form>
      {Object.keys(deductionInfo).map((name) => {
        console.log('dd info: ', deductionInfo)
        return (
          <Deductions
            key={name}
            name={name}
            total={deductionInfo[name]}
            updateInfo={updateInfo}
          />
        )
      })}
      {Object.keys(deductionInfo).map((name) => (
        <h2>
          {name} Deductions: ${deductionInfo[name]}
        </h2>
      ))}
    </Layout>
  )
}

export default Home
