import styled from 'styled-components';
import Item from '../components/Item';

const Layout = styled.div`
  display: flex;
  flex-direction: column;
  padding: 10px;
`;

const TotalForm = styled.div`
  display: flex;
  flex-direction: column;
  width: 300px;
`;

const Input = styled.input`
  float: right;
`;

const Home = () => {
  return (
    <Layout>
      <h1>Grocery Calculator</h1>
      <TotalForm>
        <div>
          Total (including tax): <Input type='text' name='name' />
        </div>
        <div>
          Tax: <Input type='text' name='name' />
        </div>
      </TotalForm>
      <TotalForm>
        <h2>Sawyer Deductions</h2>
        <Item price={3.75} />
      </TotalForm>
    </Layout>
  );
};

export default Home;
