import { useState, useEffect } from 'react'
import styled from 'styled-components'

const Layout = styled.div``

const Item = (props) => {
  const [selections, setSelections] = useState({})
  console.log(indivTotals)

  useEffect(() => {
    let newSelections = {}
    Object.keys(props.indivTotals).forEach(key => newSelections[key] = true)
    setSelections(newSelections)
  }, [])

  const handleInputChange = (event) => {
    let newSelections = {...selections}
    newSelections[event.target.key] = !selections[event.target.key]
    setSelections(newSelections)
  }

  return (
    <Layout>
      <button onClick={() => props.onDeletePress(props.id)}>X</button>
      <span style={{ padding: 10 }}>${props.price} </span>
      {Object.keys(props.indivTotals).map(key => {
          <input key={key} type='checkbox' checked={selections[key]} onChange={handleInputChange}/>
      })}
    </Layout>
  )
}

export default Item
