import { useState } from 'react';
import styled from 'styled-components';
import Item from '../components/Item';

const Layout = styled.div`
  display: flex;
  flex-direction: column;
  padding: 10px;
`;

const TotalForm = styled.div`
  display: flex;
  flex-direction: column;
  width: 300px;
`;

const Input = styled.input`
  float: right;
`;

const AddItemForm = styled.div`
  flex-direction: row;
`;

const InputButton = styled.div`
  float: right;
`;

const Home = () => {
  const [sawyerInput, setSawyerInput] = useState('');
  const [sawyerList, setSawyerList] = useState([]);

  console.log('sawyers list', sawyerList);

  const onSawyerInputChange = (event) => {
    setSawyerInput(event.target.value);
  };

  const onSawyerInputAdd = (event) => {
    setSawyerList([...sawyerList, sawyerInput]);
    event.preventDefault();
  };

  const onSawyerItemDelete = (selectedValue) => {
    console.log('selected, ', selectedValue);
    let removed = false;
    setSawyerList(
      sawyerList.filter((itemValue) => {
        if (removed) return true;
        else if (itemValue === selectedValue) {
          removed = true;
          return false;
        } else true;
      }),
    );
  };

  return (
    <Layout>
      <h1>Grocery Calculator</h1>
      <TotalForm>
        <div>
          Total (including tax):{' '}
          <Input type='text' name='name' placeholder='Enter Dollar Value' />
        </div>
        <div>
          Tax:{' '}
          <Input type='text' name='name' placeholder='Enter Dollar Value' />
        </div>
      </TotalForm>
      <TotalForm>
        <h2>Sawyer Deductions</h2>
        <AddItemForm>
          <span>Item to deduct: </span>
          <InputButton>
            <input
              onChange={onSawyerInputChange}
              type='text'
              name='name'
              placeholder='Enter Dollar Value'
            />
            <button onClick={onSawyerInputAdd}>+</button>
          </InputButton>
        </AddItemForm>
        {sawyerList.map((value) => (
          <Item key={value} price={value} onDeletePress={onSawyerItemDelete} />
        ))}
      </TotalForm>
    </Layout>
  );
};

export default Home;
