import { useState, useEffect } from 'react'
import styled from 'styled-components'
import Deductions from '../components/deductions'

const Layout = styled.div`
  display: flex;
  flex-direction: column;
  padding: 10px;
`

const Form = styled.div`
  display: flex;
  flex-direction: column;
  width: 300px;
`

const Input = styled.input`
  float: right;
`

const AddItemForm = styled.div`
  flex-direction: row;
`

const InputButton = styled.div`
  float: right;
`

const Home = () => {
  const [deductionInfo, setDeductionInfo] = useState({
    Sawyer: 0,
    Wyatt: 0,
    Jack: 0,
  })

  const [total, setTotal] = useState()
  const [tax, setTax] = useState()

  const updateInfo = (name, list) => {
    const info = { ...deductionInfo }
    const total = list.reduce((a, b) => a + b, 0)
    info[name] = total
    setDeductionInfo(info)
  }

  return (
    <Layout>
      <h1>Grocery Calculator</h1>
      <Form>
        <div>
          Total (including tax):{' '}
          <Input
            onChange={(event) => setTotal(event.target.value)}
            type='text'
            value={total}
            placeholder='Enter Dollar Value'
          />
        </div>
        <div>
          Tax:{' '}
          <Input
            onChange={(event) => setTax(event.target.value)}
            type='text'
            value={tax}
            placeholder='Enter Dollar Value'
          />
        </div>
      </Form>
      {Object.keys(deductionInfo).map((name) => (
        <Deductions
          key={name}
          name={name}
          total={deductionInfo[name]}
          updateInfo={updateInfo}
        />
      ))}
      <div>
        {Object.keys(deductionInfo).map((name) => (
          <p>
            {name} Deductions: ${deductionInfo[name]}
          </p>
        ))}
      </div>
    </Layout>
  )
}

export default Home
