import { useState } from 'react';
import styled from 'styled-components';
import Item from '../components/Item';

const Layout = styled.div`
  display: flex;
  flex-direction: column;
  padding: 10px;
`;

const TotalForm = styled.div`
  display: flex;
  flex-direction: column;
  width: 300px;
`;

const Input = styled.input`
  float: right;
`;

const AddItemForm = styled.div`
  flex-direction: row;
`;

const InputButton = styled.div`
  float: right;
`;

const Home = () => {
  const [sawyerInput, setSawyerInput] = useState('');

  const onSawyerInputChange = (event) => {
    setSawyerInput(event.target.value);
  };

  return (
    <Layout>
      <h1>Grocery Calculator</h1>
      <TotalForm>
        <div>
          Total (including tax): <Input type='text' name='name' />
        </div>
        <div>
          Tax: <Input type='text' name='name' />
        </div>
      </TotalForm>
      <TotalForm>
        <h2>Sawyer Deductions</h2>
        <AddItemForm>
          <span>Item to deduct: </span>
          <InputButton>
            <input onChange={onSawyerInputChange} type='text' name='name' />
            <button>+</button>
          </InputButton>
        </AddItemForm>
        <Item price={3.75} />
      </TotalForm>
    </Layout>
  );
};

export default Home;
