import { useState, useEffect } from 'react'
import styled from 'styled-components'

const Layout = styled.div`
  margin: 10px 0;
`

const Checkboxes = styled.div`
  display: flex;
  justify-content: space-around;
`

const Item = (props) => {
  const [selections, setSelections] = useState({})

  useEffect(() => {
    let newSelections = {}
    Object.keys(props.indivTotals).forEach((key) => (newSelections[key] = true))
    setSelections(newSelections)
  }, [])

  useEffect(() => {
    const newIndivTotals = { ...props.indivTotals }
    const numPeople = Object.keys(newIndivTotals).length
    const selectedCount = Object.values(selections).reduce(
      (accumulator, currentValue) => accumulator + currentValue,
    )
    console.log('selected count: ', selectedCount)
    Object.keys(selections).forEach((name) => {
      selections[name]
        ? (newIndivTotals[name] += props.price / numPeople / selectedCount)
        : console.log('not selected')
    })
    props.setIndivTotals(newIndivTotals)
  }, [selections])

  const handleInputChange = (key) => {
    let newSelections = { ...selections }
    console.log(key)
    newSelections[key] = !selections[key]
    setSelections(newSelections)
  }

  return (
    <Layout>
      <button onClick={() => props.onDeletePress(props.id)}>X</button>
      <span style={{ padding: 10 }}>${props.price} </span>
      <Checkboxes>
        {Object.keys(props.indivTotals).map((key) => (
          <div>
            <span>{key}: </span>
            <input
              key={key}
              type='checkbox'
              checked={selections[key]}
              onChange={() => handleInputChange(key)}
            />
          </div>
        ))}
      </Checkboxes>
    </Layout>
  )
}

export default Item
