import styled from 'styled-components';
import Item from '../components/Item';

const Layout = styled.div`
  display: flex;
  flex-direction: column;
  padding: 10px;
`;

const TotalForm = styled.div`
  display: flex;
  flex-direction: column;
  width: 300px;
`;

const Input = styled.input`
  float: right;
`;

const Home = () => {
  return (
    <Layout>
      <h1>Grocery Calculator</h1>
      <TotalForm>
        <div>
          Total (including tax): <Input type='text' name='name' />
        </div>
        <div>
          Tax: <Input type='text' name='name' />
        </div>
        <button>Submit</button>
      </TotalForm>
      <h2>Sawyer Deductions</h2>
      <p>enter prices of items to be removed from Sawyer's total</p>
      <Item price={3.75} />
    </Layout>
  );
};

export default Home;
