import { useState, useEffect } from 'react'
import styled from 'styled-components'
import Deductions from '../components/deductions'

const Layout = styled.div`
  display: flex;
  flex-direction: column;
  padding: 10px;
`

const Form = styled.div`
  display: flex;
  flex-direction: column;
  width: 300px;
`

const Input = styled.input`
  float: right;
`

const AddItemForm = styled.div`
  flex-direction: row;
`

const InputButton = styled.div`
  float: right;
`

const Home = () => {
  const [deductionInfo, setDeductionInfo] = useState(
    [
      {name: 'Sawyer', value: 0},
      {name: 'Wyatt', value: 0},
      {name: 'Jack', value: 0},
    ]
  )

  return (
    <Layout>
      <h1>Grocery Calculator</h1>
      <Form>
        <div>
          Total (including tax):{' '}
          <Input type='text' placeholder='Enter Dollar Value' />
        </div>
        <div>
          Tax: <Input type='text' placeholder='Enter Dollar Value' />
        </div>
      </Form>
      {deductionInfo}
      <Deductions name='Sawyer' />
    </Layout>
  )
}

export default Home
