import styled from 'styled-components'
import Item from '../components/Item'

const Layout = styled.div``

const Form = styled.div`
  display: flex;
  flex-direction: column;
  width: 300px;
`

const AddItemForm = styled.div`
  flex-direction: row;
`

const InputButton = styled.div`
  float: right;
`

const Deductions = (props) => {
  const [input, setInput] = useState('')
  const [list, setList] = useState([])

  const onInputChange = (event) => {
    setInput(event.target.value)
  }

  const onInputAdd = (event) => {
    setList([...list, input])
    setInput('')
    event.preventDefault()
  }

  return (
    <Layout>
      <Form>
        <h2>{props.name} Deductions</h2>
        <AddItemForm>
          <span>Item to deduct: </span>
          <InputButton>
            <input
              onChange={onInputChange}
              type='text'
              value={input}
              placeholder='Enter Dollar Value'
            />
            <button onClick={onInputAdd}>+</button>
          </InputButton>
        </AddItemForm>
        {list.map((value, index) => (
          <Item key={index} price={value} onDeletePress={onItemDelete} />
        ))}
      </Form>
    </Layout>
  )
}

export default Deductions
