import { useState, useEffect } from 'react'
import styled from 'styled-components'
import Item from '../components/Item'

const Layout = styled.div``

const Form = styled.div`
  display: flex;
  flex-direction: column;
  width: 300px;
`

const AddItemForm = styled.div`
  flex-direction: row;
`

const InputButton = styled.div`
  float: right;
`

const Deductions = (props) => {
  const [total, setTotal] = useState(0)
  const [input, setInput] = useState('')
  const [list, setList] = useState([])

  const onInputChange = (event) => {
    setInput(event.target.value)
  }

  const onInputAdd = (event) => {
    if (input !== '') {
      setList([...list, parseFloat(input)])
      setInput('')
      setTotal(list.reduce((a, b) => a + b, 0))
    }
    event.preventDefault()
  }

  const onItemDelete = (id) => {
    const updatedList = list.filter((item, index) => id != index)
    setList(updatedList)
    setTotal(list.reduce((a, b) => a + b, 0))
  }

  return (
    <Layout>
      <Form>
        <h2>
          {props.name} Deductions: ${total}
        </h2>
        <AddItemForm>
          <span>Item to deduct: </span>
          <InputButton>
            <input
              onChange={onInputChange}
              type='text'
              value={input}
              placeholder='Enter Dollar Value'
            />
            <button onClick={onInputAdd}>+</button>
          </InputButton>
        </AddItemForm>
        {list.map((value, index) => (
          <Item
            key={index}
            id={index}
            price={value}
            onDeletePress={onItemDelete}
          />
        ))}
      </Form>
    </Layout>
  )
}

export default Deductions
