import { useState } from 'react';
import styled from 'styled-components';
import Item from '../components/Item';

const Layout = styled.div`
  display: flex;
  flex-direction: column;
  padding: 10px;
`;

const TotalForm = styled.div`
  display: flex;
  flex-direction: column;
  width: 300px;
`;

const Input = styled.input`
  float: right;
`;

const AddItemForm = styled.div`
  flex-direction: row;
`;

const InputButton = styled.div`
  float: right;
`;

const Home = () => {
  const [sawyerInput, setSawyerInput] = useState('');
  const [sawyerList, setSawyerList] = useState([]);

  const onSawyerInputChange = (event) => {
    setSawyerInput(event.target.value);
  };

  const onSawyerInputAdd = (event) => {
    setSawyerList([...sawyerList, sawyerInput]);
    event.preventDefault();
  };

  return (
    <Layout>
      <h1>Grocery Calculator</h1>
      <TotalForm>
        <div>
          Total (including tax):{' '}
          <Input type='text' name='name' placeholder='Enter Dollar Value' />
        </div>
        <div>
          Tax:{' '}
          <Input type='text' name='name' placeholder='Enter Dollar Value' />
        </div>
      </TotalForm>
      <TotalForm>
        <h2>Sawyer Deductions</h2>
        <AddItemForm>
          <span>Item to deduct: </span>
          <InputButton>
            <input
              onChange={onSawyerInputChange}
              type='text'
              name='name'
              placeholder='Enter Dollar Value'
            />
            <button onPress={onSawyerInputAdd}>+</button>
          </InputButton>
        </AddItemForm>
        <Item price={3.75} />
      </TotalForm>
    </Layout>
  );
};

export default Home;
